import React, {useState, useEffect}from 'react';
import axios from 'axios';
import Container from 'react-bootstrap/Container';
import Row from 'react-bootstrap/Row';
import Form from 'react-bootstrap/Form';
import Button from 'react-bootstrap/Button';
// import MemberForm from './MemberForm';
import useTeamForm from '../CustomHooks';
import {useParams} from "react-router-dom";

const EditMember = () => {
    const [member, setMember] = useState({});
    const [loading, setLoading] = useState(true);
    // const [name, setName] = useState(member.name);
    let { id } = useParams();

    useEffect(() => {
        const getMember = () => {
            axios
                .get(`http://localhost:8082/team/${id}`)
                .then(res => {
                    setMember(res.data);
                })
                .catch(err => console.log('error', err))
        }
        getMember();
        console.log(member)
        setLoading(false);
    }, [id]);

    // const setInitialValue = member.name;


    // const handleInput = (e) => {
    //     e.preventDefault();
    //     setName(e.target.value)

    // }

    const edit = () => {
        axios
            .put(`http://localhost:8082/team/${id}`, inputs)
            .catch(err => console.log('error', err))
    }


    const {inputs, handleInputChange, handleSubmit} = useTeamForm(edit);

    return (
        (!loading && 
            <Container>
                <Row>
                    <Form onSubmit={handleSubmit}>
                    <h3>{`Edit Team Memeber ${member.name}`}</h3>
                        <Form.Group controlId='name'>
                            <Form.Label>Team Member Name</Form.Label>
                            <Form.Control onChange={handleInputChange} value={member.name} type='text' name='name' placeholder="Enter Name" />
                        </Form.Group>
                        {/* <Form.Group controlId='title'>
                            <Form.Label>Team Member Title</Form.Label>
                            <Form.Control onChange={handleInputChange} type='text' name='title' placeholder="Enter Title" />
                        </Form.Group>
                        <Form.Group controlId='bio'>
                            <Form.Label>Team Member Bio</Form.Label>
                            <Form.Control onChange={handleInputChange} as='textarea' rows='5' name='bio' placeholder="Enter Bio" />
                        </Form.Group>
                        <Form.Group controlId='image'>
                            <Form.Label>Team Member Photo</Form.Label>
                            <Form.Control onChange={handleInputChange} type='text'  name='image' placeholder="Enter Photo Link" />
                        </Form.Group> */}
                        <Button type='submit'>Submit</Button>
                    </Form>
                </Row>
            </Container>
        )
    )
}

export default EditMember;
