import React, {useState, useEffect} from 'react';
import axios from 'axios';
import Container from 'react-bootstrap/Container';
import Row from 'react-bootstrap/Row';

const ShowTeam = () => {
    const [members, setMembers] = useState([]);
    const [loading, setLoading] = useState(true);

    useEffect(() => {
        const getTeam = () => {
            axios
                .get('http://localhost:8082/team')
                .then(res => {
                    setMembers(res.data);
                })
                .catch(err => console.log('error', err))
        }
        getTeam();
        setLoading(false);
    }, []);


    function onDeleteClick (id) {
        console.log('delete route clicked')
        axios
          .delete('http://localhost:8082/team/'+id)
          .catch(err => {
            console.log("Error form ShowBookDetails_deleteClick");
          })
      };

    // onDeleteClick (id) {
    //     console.log(id);
    //     axios
    //         .delete(`http://localhost:8082/team/${id}`)
    //         .catch(err => console.log('error', err))
    // }

    return (
        (!loading && <Container>
            <Row>
                <h1>Team</h1>
            </Row>
            <Row>
                <ul>
                    {members.map(member => {
                        return (
                            <div key={member.name}>
                                <li className='name'>{member._id}</li>
                                <li className='name'>{member.name}</li>
                                <li className='title'>{member.title}</li>
                                <li className='bio'>{member.bio}</li>
                                <button onClick={onDeleteClick.bind(this,member._id)}>Delete</button>
                                {/* <img src={member.image} alt='portrait of team member' /> */}
                                <br />
                            </div>

                        )
                    })}
                </ul>
            </Row>
        </Container>)
    )
}

export default ShowTeam;
