import React, {useState, useEffect}from 'react';
import axios from 'axios';
import Container from 'react-bootstrap/Container';
import Row from 'react-bootstrap/Row';
import MemberForm from './MemberForm';
import useTeamForm from '../CustomHooks';

const EditMember = () => {
    const [member, setMember] = useState({});
    const [loading, setLoading] = useState(true);

    useEffect(() => {
        const getMember = () => {
            axios
                // .get(`http://localhost:8082/team/${this.match.params.id}/edit`)
                .then(res => {
                    setMember(res.data);
                })
                .catch(err => console.log('error', err))
        }
        getMember();
        setLoading(false);
        // console.log(this.match.params.name)
    }, []);

    console.log(member);
    const edit = () => {
        axios
            .put('http://localhost:8082/team', inputs)
            .catch(err => console.log('error', err))
    }

    const {inputs, handleInputChange, handleSubmit} = useTeamForm(edit);

    return (
        (!loading && 
            <Container>
                <Row>
                    <MemberForm 
                        title="Edit Member"
                        handleInputChange={handleInputChange}
                        handleSubmit={handleSubmit}
                        callback={edit}
                        inputs={inputs}
                    />
                </Row>
        </Container>
            )

    )
}

export default EditMember;
