import React, {useState, useEffect}from 'react';
import axios from 'axios';
import useTeamForm from '../CustomHooks';
import {useParams} from "react-router-dom";
import MemberForm from './MemberForm';

const EditMember = () => {
    const [member, setMember] = useState({});
    const [loading, setLoading] = useState(true);
    let { id } = useParams();

    const fetchMember = () => {
        axios
                .get(`http://localhost:8082/team/${id}`)
                .then(res => {
                    setMember(res.data);
                })
                .catch(err => console.log('error', err))
    }

useEffect(() => {
    async function fetchData(){
        await fetchMember();
        console.log(member)
    }
    fetchData()
    setLoading(false)
})

    // useEffect(async function fetch() {
    //     const data = await fetchMember();
    //     console.log
    // })

    const edit = () => {
        axios
            .put(`http://localhost:8082/team/${id}`, inputs)
            .catch(err => console.log('error', err))
    }

    const {inputs, handleInputChange, handleSubmit} = useTeamForm(edit);

    return (
        (!loading && 
            <MemberForm 
                method = 'edit'
                title = {`Edit Team Member: ${member.name}`}
                inputs = {inputs}
                handleInputChange={handleInputChange}
                handleSubmit={handleSubmit}
                member={member}
            />
        )
    )
}

export default EditMember;






